mapper.select:
SELECT i.* FROM items i INNER JOIN users u ON i.user_no=u.user_no
and i.item_category_no=#{category_no}
and u.user_dong=#{user_dong}
and i.item_status=#{item_status}
and i.item_price >= #{price_min}
and i.item_price <= #{price_max}
ORDER BY i.item_no DESC
ORDER BY i.item_hits DESC
ORDER BY i.item_price DESC
ORDER BY i.item_price ASC
ORDER BY i.item_no DESC
LIMIT #{start},30

SELECT b.* FROM boards b INNER JOIN users u ON b.user_no=u.user_no
b.meet_no=#{meet_no}
and b.board_category_no=#{category_no}
and u.user_dong=#{user_dong}
ORDER BY b.board_no DESC
ORDER BY b.board_hits DESC
ORDER BY b.board_no DESC
LIMIT #{start},30

SELECT m.* FROM meets m INNER JOIN users u ON m.host_user_no=u.user_no
and m.meet_category=#{category_no}
and u.user_dong=#{user_dong}
ORDER BY m.meet_no DESC LIMIT #{start},30

SELECT *
FROM users
WHERE user_no=#{user_no}

SELECT *
FROM items
WHERE item_no=#{item_no}

SELECT *
FROM board_category
WHERE board_category_no=#{board_category_no}

SELECT *
FROM item_category
WHERE item_category_no=#{item_category_no}

SELECT *
FROM meet_category
WHERE meet_category_no=#{meet_category_no}

SELECT COUNT(*)
FROM likes
WHERE board_no=#{board_no}

SELECT COUNT(*)
FROM comments
WHERE board_no=#{board_no}

SELECT COUNT(*)
FROM meet_users
WHERE meet_no=#{meet_no}
